property.code:
document.observe('xwiki:dom:loaded', function() {

    var fetchAnnotations = function(text) {
      var queryString = '?outputSyntax=plain&text=' + encodeURIComponent(sampleText);
      new Ajax.Request(new XWiki.Document('AnnotationService', 'PhenoTips').getURL('get') + queryString,
        {
          onCreate : function() {
            // create loading text
          },
          onSuccess : function(response) {
            var data = response.responseJSON;
            for (var annotation in data) {
              if (data.hasOwnProperty(annotation)) {
                // remove all mentioned phenotypes
              }
            }
            updateSuggestionBoxes(data);
          },
          onComplete : function () {
            // remove "loading" text
          }
        });
    };

    var updateSuggestionBoxes = function(annotations) {
      suggestionsContainer.update(
      new Element('table', {"class" : "suggestions-table"}).insert(
        new Element('tr').insert(
          new Element('td', {"class" : tdClass}).insert(generateSuggestion(annotations))
        ).insert(
          new Element('td', {"class" : tdClass}).insert(generateSuggestion(annotations))
        )
      ).insert(
        new Element('tr').insert(
          new Element('td', {"class" : tdClass}).insert(generateSuggestion(annotations))
        ).insert(
          new Element('td', {"class" : tdClass}).insert(generateSuggestion(annotations))
        )
      ));
    }

    // generate suggestion from first suggestion in current suggestions list
    var generateSuggestion = function(annotations, text) {
      var nextSuggestion = annotations.shift();
      if(nextSuggestion) {
        var start = nextSuggestion.start;
        var end = nextSuggestion.end;
        var quoteStart = Math.max(0, start - 22);
        var quoteEnd = Math.min(start + 22, text.length)

        return new Element('div', {'class' : 'suggestion'}).insert(
          new Element('div', {'class' : 'suggestion-headline'}).insert(
            '<span class="yes-no-picker initialized"><label class="yes" title="YES (investigated and observed)" for=""><input type="checkbox" value="HP:0001142" title="Lenticonus" name="PhenoTips.PatientClass_0_phenotype" id="" style="display: none;">Y</label><label class="no" title="NO (investigated and NOT observed)" for=""><input type="checkbox" value="HP:0001142" title="Lenticonus" name="PhenoTips.PatientClass_0_negative_phenotype" id="" style="display: none;">N</label></span>'
            ).insert(
              new Element("span", {"class" : "suggestion-term"}).update(text.substring(start, end).replace(/\w\S*/g, function(txt){return txt.charAt(0).toUpperCase() + txt.substr(1).toLowerCase();}))
            ).insert('<span class="xHelpButton fa fa-info-circle phenotype-info" title="About this phenotype"></span>')
        ).insert(
          new Element('div', {'class' : 'suggestion-quote'}).insert(
            "... " + text.substring(quoteStart, start)
          ).insert(
            new Element('span', {'class' : 'quoted-term'}).update(text.substring(start, end))
          ).insert(
            text.substring(end, quoteEnd) + " ..."
          )
        ).insert(
          new Element('div', {'class' : 'hide-suggestion'}).update("âœ–")
        )
      }

      console.log("Out of Suggestions");
      return null;
    }

    var suggestionsContainer = new Element('div', {'id' : 'suggestions-container'});
    var tdClass = "suggestion-table-cell";
    var clinicalNotesSubPanel = 
      new Element('div', {"class" : "sub-panel"}).insert(
        new Element('h3', {"class" : "wikigeneratedheader"}).update(
          "<span><strong>Suggestions from Clinical Notes</strong></span>"
        )
      );
    clinicalNotesSubPanel.insert(suggestionsContainer);
    clinicalNotesSubPanel.insert(new Element("div", {"id" : "suggestion-count"}).update("4 of 15 suggestions"));
    $$(".current-phenotype-selection")[0].insert({top : clinicalNotesSubPanel});

    // generate innitial suggestions
    var sampleText = "Johnathan ate seven apples using his blue eyes, but he was hearing impaired";
    var annotations = [];
    fetchAnnotations(sampleText);
});

